import UIKit

class NewsUpdateViewController: UIViewController {

    // MARK: Properties

    var newsUpdates: [NewsUpdate] = []
    var userDemographics: UserDemographics?

    // MARK: View Lifecycle

    override func viewDidLoad() {
        super.viewDidLoad()

        // Load news updates
        fetchNewsUpdates()

        // Set up user demographics
        setupUserDemographics()
    }

    // MARK: Actions

    @objc func fetchNewsUpdates() {
        // Fetch news updates from API
        let apiUrl = "https://example.com/news_updates"
        let apiRequest = URLRequest(url: apiUrl)
        let session = URLSession(configuration: .default)
        let dataTask = session.dataTask(with: apiRequest) { data, response, error in
            // Handle response data
            if let error = error {
                print("Error fetching news updates: \(error.localizedDescription)")
                return
            }
            let decodedData = try! JSONDecoder().decode([NewsUpdate].self, from: data)
            DispatchQueue.main.async {
                self.newsUpdates = decodedData
                self.updateUI()
            }
        }
        dataTask.resume()
    }

    @objc func updateUI() {
        // Update UI with news updates
        let newsUpdateLabel = UILabel(frame: .zero)
        newsUpdateLabel.text = "Latest news updates:"
        view.addSubview(newsUpdateLabel)

        for newsUpdate in newsUpdates {
            let label = UILabel(frame: .zero)
            label.text = newsUpdate.title
            label.textColor = newsUpdate.color
            label.sizeToFit()
            view.addSubview(label)
        }
    }

    // MARK: User Interface

    @IBOutlet weak var newsUpdateLabel: UILabel! {
        didSet {
            updateUI()
        }
    }

    // MARK: User Demographics

    func setupUserDemographics() {
        // Set up user demographics based on user input
        let userAge = UserDefaults.standard.integer(forKey: "age")
        let userIncome = UserDefaults.standard.integer(forKey: "income")
        let userOccupation = UserDefaults.standard.string(forKey: "occupation")

        // Update user demographics
        userDemographics = UserDemographics(age: userAge, income: userIncome, occupation: userOccupation)
    }
}

